Bash(TZ=":UTC" node -e "
const BRAZIL_TZ = ''America/Sao_Paulo'';
let TZ = process.env.TZ || BRAZIL_TZ;

function validateTimezone(tz) {
  try {
    new Date().toLocaleString(''en-US'', {timeZone: tz});
    return tz;
  } catch (error) {
    console.log(''Invalid timezone detected:'', tz);
    return BRAZIL_TZ;
  }
}

if (TZ === '':UTC'' || TZ.startsWith('':'') || TZ === ''UTC'') {
  console.log(''Vercel timezone detected:'', TZ, ''-> using'', BRAZIL_TZ);
  TZ = BRAZIL_TZ;
} else {
  TZ = validateTimezone(TZ);
}

console.log(''Final timezone:'', TZ);
const today = new Date();
const brazilToday = new Date(today.toLocaleString(''en-US'', {timeZone: TZ}));
console.log(''Success! Brazil time:'', brazilToday.toISOString());
")

