Bash(TZ="America/Sao_Paulo" node -e "
const now = new Date();
const brazilNow = new Date(now.toLocaleString(''en-US'', {timeZone: ''America/Sao_Paulo''}));
console.log(''Current UTC time:'', now.toISOString());
console.log(''Current Brazil time:'', brazilNow.toISOString());
console.log(''Brazil date (YYYY-MM-DD):'', brazilNow.toISOString().split(''T'')[0]);
console.log(''Weekday in Brazil:'', brazilNow.toLocaleDateString(''pt-BR'', {weekday: ''long'', timeZone: ''America/Sao_Paulo''}));
")

